<?phpinclude_once(SCRIPT_ROOT_DIR . '/phpQuery.php');if (!class_exists('phpQuery')){    echo 'NO PHP QUERY LIB. Exiting.' . PHP_EOL;    die(__FILE__ . ' : ' . __LINE__);}/* ------------Functions--------------- */function getHtml($url = ''){    if (empty($url))    {        return '';    }    if (function_exists('curl_init'))    {        //return getCurl( $url );    }    $opts = array(        'http' => array(            'method' => 'GET',            'header' =>                         "Content-type: application/x-www-form-urlencoded;charset=iso-8859-1" .                        "Accept-language: de\r\n" .                        "Cookie: foo=bar\r\n"        )    );    $context = stream_context_create($opts);    // Open the file using the HTTP headers set above    if (!$html = file_get_contents($url, false, $context))    {        return false;    }    return $html;}function getCurl($url = ''){    $ch = curl_init($url);    curl_setopt($ch, CURLOPT_HEADER, 0);    curl_setopt($ch, CURLOPT_POST, 1);    curl_setopt($ch, CURLOPT_RETURNTRANSFER, 1);    $output = curl_exec($ch);    curl_close($ch);    return $output;}function prepareLink($link){    $link = preg_replace('/(.*)(;jsessionid.*)$/', '$1', $link);    return $link;}function _die($message){    ob_end_flush();    echo $message . PHP_EOL;    die(__FILE__ . ' : ' . __LINE__);}function _redirect($location, array $params = array()){    if ( !empty( $params ) )    {        $location .= substr_count($location, '?') > 0 ? '' : '?1';        foreach ( $params as $key => $param )        {            $location .= '&' . $key . '=' . $param;        }		    }    ob_end_clean();    header('Location: ' . $location);    die;}?>